import { RoomCategoryDTO, RoomDTO } from '@/app/data/room'
import useFetchData from '@/app/hooks/useFetchData'
import { roomStatusAsResponse, roomTypeAsResponse } from '@/app/utils/responseAsStatus'
import { Descriptions, Image, List, Result, Skeleton, Tag, Typography } from 'antd'
import React from 'react'

type RoomDetailsProps = {
  id: string
}

const RoomDetails: React.FC<RoomDetailsProps> = ({ id }) => {
  // fetch room-details API data
  const [loading, error, response] = useFetchData(`/room/${id}`)

  const room: RoomDTO = response ? response : []
  return (
    <Skeleton loading={loading} paragraph={{ rows: 10 }} active avatar>
      {error ? (
        <Result title='Failed to fetch' subTitle={error} status='error' />
      ) : (
        <Descriptions title='Room Information' bordered>
          <Descriptions.Item label='Images' span={3}>
            <Image
              style={{ width: '100px', height: '100px' }}
              src={room ? room.image : 'error'}
              fallback='data:image/png;base64,'
              crossOrigin='anonymous'
              alt='user-image'
            />
          </Descriptions.Item>

          <Descriptions.Item label={<span className='whitespace-nowrap'>Room Name</span>} span={2}>
            {room ? room.name : 'Default Room Name'}
          </Descriptions.Item>

          <Descriptions.Item label={<span className='whitespace-nowrap'>Room Type</span>}>
            <Tag
              className='w-[60px] !text-center uppercase'
              color={roomTypeAsResponse(room?.roomCategoryDTO?.name).color}
            >
              {roomTypeAsResponse(room?.roomCategoryDTO?.name).level}
            </Tag>
          </Descriptions.Item>
          <Descriptions.Item label={<span className='whitespace-nowrap'>Room Price</span>} span={2}>
            {`${
              room && room.price
                ? room.price.toLocaleString('vi-VN', {
                    style: 'currency',
                    currency: 'VND'
                  })
                : Number(0).toLocaleString('vi-VN', {
                    style: 'currency',
                    currency: 'VND'
                  })
            }`}
          </Descriptions.Item>

          <Descriptions.Item label={<span className='whitespace-nowrap'>Room Status</span>} span={2}>
            <Tag className='w-[80px] !text-center uppercase' color={roomStatusAsResponse(room?.rent).color}>
              {roomStatusAsResponse(room?.rent).level}
            </Tag>
          </Descriptions.Item>
        </Descriptions>
      )}
    </Skeleton>
  )
}

const Index = React.memo(RoomDetails)
export default Index
